import React, { useState, useEffect } from 'react';

const Feedback = () => {
  const [feedback, setFeedback] = useState('');
  const [feedbackList, setFeedbackList] = useState([]);
  const [loading, setLoading] = useState(true);

  // Função para buscar feedbacks do servidor
  const fetchFeedbacks = async () => {
    try {
      const response = await fetch('http://localhost:8000/feedback');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setFeedbackList(data);
    } catch (error) {
      console.error('Erro ao buscar feedbacks:', error);
    } finally {
      setLoading(false);
    }
  };

  // Função para enviar feedback ao servidor
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!feedback) return; // Não enviar se o feedback estiver vazio

    try {
      const response = await fetch('http://localhost:8000/feedback', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ feedback }),
      });

      if (!response.ok) {
        throw new Error('Erro ao enviar feedback');
      }

      // Limpar o campo de feedback
      setFeedback('');
      // Recarregar a lista de feedbacks
      fetchFeedbacks();
    } catch (error) {
      console.error('Erro ao enviar feedback:', error);
    }
  };

  // Carregar feedbacks quando o componente é montado
  useEffect(() => {
    fetchFeedbacks();
  }, []);

  return (
    <div>
      <h1>Feedback</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          value={feedback}
          onChange={(e) => setFeedback(e.target.value)}
          placeholder="Deixe seu feedback aqui..."
          required
        />
        <button type="submit">Enviar Feedback</button>
      </form>

      <h2>Feedbacks Recebidos</h2>
      {loading ? (
        <p>Carregando...</p>
      ) : (
        <ul>
          {feedbackList.map((item, index) => (
            <li key={index}>{item.feedback}</li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Feedback;
