import React, { useState } from "react";
import axios from "axios";

const Feedback = () => {
    const [feedbackText, setFeedbackText] = useState("");
    const [message, setMessage] = useState("");
    const [error, setError] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();

        const userId = localStorage.getItem("userId");  // Exemplo de como pegar o ID do usuário logado

        if (!userId) {
            setError("Você precisa estar logado para enviar um feedback.");
            return;
        }

        try {
            const response = await axios.post("http://localhost:8000/feedback/create", 
                { userId, feedbackText }, 
                { headers: { "Content-Type": "application/json" } }
            );

            setMessage(response.data.message);
            setError("");  // Limpa a mensagem de erro
            setFeedbackText("");  // Limpa o campo de feedback
        } catch (err) {
            setError(err.response?.data?.message || "Erro ao enviar feedback");
            setMessage("");  // Limpa a mensagem de sucesso
        }
    };

    return (
        <div>
            <h1>Deixe seu Feedback</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="feedback">Feedback:</label>
                    <textarea
                        id="feedback"
                        value={feedbackText}
                        onChange={(e) => setFeedbackText(e.target.value)}
                        required
                    />
                </div>

                <button type="submit">Enviar Feedback</button>
            </form>

            {message && <p style={{ color: "green" }}>{message}</p>}
            {error && <p style={{ color: "red" }}>{error}</p>}
        </div>
    );
};

export default Feedback;
