import React, { useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios"; // Se você quiser usar o Axios

const Register = () => {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [message, setMessage] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Enviar os dados para o backend
            const response = await axios.post("http://localhost:8000/usuario/registro", { name, email, password });

            // Se tudo ocorrer bem
            setMessage(response.data.message);
            setError("");  // Limpa a mensagem de erro
        } catch (err) {
            // Tratar erros
            console.error( 'ESSE FOI O ERRO: ' ,err);
            setError(err.response?.data?.message || "Erro ao registrar usuário");
            setMessage("");  // Limpa a mensagem de sucesso
        }
    };

    return (
        <div>
            <h1>Cadastro</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="name">Nome:</label>
                    <input
                        type="text"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>

                <div>
                    <label htmlFor="email">E-mail:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>

                <div>
                    <label htmlFor="password">Senha:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>

                <button type="submit">Cadastrar</button>

                {/* caso o usuário tenha cadastro de a opção de se logar redirecionando para a páginad e login */}
                <p>Já tem uma conta? <Link to="/login">Faça login</Link></p>
            </form>

            {/* {message && <p style={{ color: "green" }}>{message}</p>} */}
            {message && setTimeout(() => {setMessage(""); window.location.href = "/";})}

            {error && <p style={{ color: "red" }}>{error}</p>}
        </div>
    );
};

export default Register;
