import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Feedback = () => {
    const [feedbackText, setFeedbackText] = useState("");
    const [message, setMessage] = useState("");
    const [error, setError] = useState("");
    const [feedbacks, setFeedbacks] = useState([]);

    // Função para buscar todos os feedbacks
    const fetchFeedbacks = async () => {
        try {
            const response = await axios.get("http://localhost:8000/usuario/feedback/all");
            setFeedbacks(response.data); // Armazena os feedbacks no estado
        } catch (err) {
            console.error("Erro ao buscar feedbacks:", err);
        }
    };

    useEffect(() => {
        fetchFeedbacks(); // Busca os feedbacks ao carregar a página
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post(
                "http://localhost:8000/usuario/feedback/create",
                { feedbackText },
                { headers: { "Content-Type": "application/json" } }
            );

            setMessage(response.data.message);
            setError(""); 
            setFeedbackText(""); 

            fetchFeedbacks(); // Atualiza a lista de feedbacks após enviar um novo
        } catch (err) {
            setError(err.response?.data?.message || "Erro ao enviar feedback");
            setMessage("");
        }
    };

    return (
        <div>
            <h1>Deixe seu Feedback</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="feedback">Feedback:</label>
                    <textarea
                        id="feedback"
                        value={feedbackText}
                        onChange={(e) => setFeedbackText(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Enviar Feedback</button>
            </form>

            {message && <p style={{ color: "green" }}>{message}</p>}
            {error && <p style={{ color: "red" }}>{error}</p>}

            {/* de a opção de ir para a página home */}
            <Link to="/">Ir para Home</Link>

            <h2>Feedbacks anteriores</h2>
            <ul>
                {feedbacks.map((fb) => (
                    <li key={fb.id}>
                        <p><strong>Data:</strong> {fb.createdAt}</p>
                        <p>{fb.feedbackText}</p>
                        <hr />
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Feedback;
