// Login.js

import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import { getAuth, signInWithEmailAndPassword } from 'firebase/auth';
import app from '../firebase-config';  // Importe a inicialização do Firebase
import './login.css'; // Importe o arquivo CSS

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [message, setMessage] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        // Verificar se o usuário já está logado ao acessar a página de login
        const checkSession = async () => {
            try {
                const response = await axios.get("http://localhost:8000/usuario/check-session", {
                    withCredentials: true,
                });

                if (response.status === 200) {
                    navigate('/feedback');  // Redireciona para a página de feedback caso esteja logado
                }
            } catch (error) {
                // Se não estiver logado, continua na página de login
                console.error(error);
            }
        };

        checkSession();
    }, [navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Inicializar o Firebase Auth
            const auth = getAuth(app);  // Certifique-se de passar a instância do app
            // Autenticar com Firebase
            const userCredential = await signInWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;

            // Obter o token do Firebase para enviar para o backend
            const idToken = await user.getIdToken();

            // Enviar o token ao backend
            const response = await axios.post("http://localhost:8000/usuario/login", { token: idToken }, {
                withCredentials: true,  // Habilita o envio do cookie de sessão
                headers: {
                    "Content-Type": "application/json",
                },
            });

            setMessage(response.data.message);
            setError("");

            // Redireciona para a página de feedback após o login
            navigate('/feedback');
        } catch (err) {
            console.error('ESSE FOI O ERRO: ', err);
            setError(err.response?.data?.message || "Erro ao fazer login");
            setMessage("");
        }
    };

    return (
        <div className="login-container">
            <form className="login-form" onSubmit={handleSubmit}>
                <h1>Login</h1>
                <div className="form-group">
                    <label htmlFor="email">E-mail:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        placeholder="Digite seu e-mail"
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="password">Senha:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        placeholder="Digite sua senha"
                    />
                </div>

                <button type="submit" className="login-button">Entrar</button>

                <Link to="/register" className="register-link">Cadastre-se</Link>

                {message && (
                    <div className="message success">
                        {message}
                    </div>
                )}

                {error && (
                    <div className="message error">
                        {error}
                    </div>
                )}
            </form>
        </div>
    );
};

export default Login;
