import React, { useEffect, useState } from "react";
import axios from "axios";
// importar o navigate
import { navigate } from "gatsby";

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [message, setMessage] = useState("");


    useEffect(() => {
        // Verificar se o usuário já está logado
        const checkSession = async () => {
            try {
                const response = await axios.get("http://localhost:8000/usuario/check-session", {
                    withCredentials: true,
                });

                if (response.status === 200) {
                    navigate("/");
                }
            }
            catch (error) {
            }
        };

        checkSession();
    }
    , []);
                



    const handleSubmit = async (e) => {
        e.preventDefault();


        try {
            // Enviar os dados para o backend na URL correta
            const response = await axios.post("http://localhost:8000/usuario/login", { email, password }, {
                withCredentials: true,
                headers: {
                    "Content-Type": "application/json",
                },
            });
            

            // Se tudo ocorrer bem
            setMessage(response.data.message);
            setError("");  // Limpa a mensagem de erro
        } catch (err) {
            // Tratar erros
            setError(err.response?.data?.message || "Erro ao fazer login");
            setMessage("");  // Limpa a mensagem de sucesso
        }
    };

    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="email">E-mail:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>

                <div>
                    <label htmlFor="password">Senha:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>

                <button type="submit">Entrar</button>
            </form>

            {message && <p style={{ color: "green" }}>{message}</p>}

            {message && setTimeout(() => {
                window.location.href = "/";
            }, 1000)}

            {error && <p style={{ color: "red" }}>{error}</p>}
        </div>
    );
};

export default Login;
