import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // Importe o hook useNavigate
import { Link } from "react-router-dom";
import axios from "axios";

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [message, setMessage] = useState("");
    const navigate = useNavigate(); // Utilize o hook useNavigate

    useEffect(() => {
        // Verificar se o usuário já está logado ao acessar a página de login
        const checkSession = async () => {
            try {
                const response = await axios.get("http://localhost:8000/usuario/check-session", {
                    withCredentials: true,
                });

                if (response.status === 200) {
                    navigate('/feedback');  // Redireciona para a página de feedback caso esteja logado
                }
            } catch (error) {
                // Se não estiver logado, continua na página de login
            }
        };

        checkSession();
    }, [navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Autenticar com Firebase
            const userCredential = await firebase.auth().signInWithEmailAndPassword(email, password);
            const user = userCredential.user;

            // Obter o token do Firebase para enviar para o backend
            const idToken = await user.getIdToken();

            // Enviar o token ao backend
            const response = await axios.post("http://localhost:8000/usuario/login", { token: idToken }, {
                withCredentials: true,  // Habilita o envio do cookie de sessão
                headers: {
                    "Content-Type": "application/json",
                },
            });

            setMessage(response.data.message);
            setError("");

            // Redireciona para a página de feedback após o login
            navigate('/feedback');
        } catch (err) {
            setError(err.response?.data?.message || "Erro ao fazer login");
            setMessage("");
        }
    };

    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="email">E-mail:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>

                <div>
                    <label htmlFor="password">Senha:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>

                <button type="submit">Entrar</button>

                {/* caso o usuário não tiver cadastro de a opção dele se cadastrar redirecionando para a página de register */}
                <Link to="/register">Cadastre-se</Link>
            </form>

            {message && setTimeout(() => {setMessage(""); window.location.href = "/";}, 1000)}
            {error && <p style={{ color: "red" }}>{error}</p>}
        </div>
    );
};

export default Login;
