import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "./feedback.css"; // Importando o arquivo CSS

const Feedback = () => {
    const [feedbackText, setFeedbackText] = useState("");
    const [message, setMessage] = useState("");
    const [error, setError] = useState("");
    const [feedbacks, setFeedbacks] = useState([]);

    const fetchFeedbacks = async () => {
        try {
            const response = await axios.get("http://localhost:8000/usuario/feedback/all");
            setFeedbacks(response.data);
        } catch (err) {
            console.error("Erro ao buscar feedbacks:", err);
        }
    };

    useEffect(() => {
        fetchFeedbacks();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post(
                "http://localhost:8000/usuario/feedback/create",
                { feedbackText },
                { headers: { "Content-Type": "application/json" } }
            );

            setMessage(response.data.message);
            setError(""); 
            setFeedbackText(""); 
            fetchFeedbacks();
        } catch (err) {
            setError(err.response?.data?.message || "Erro ao enviar feedback");
            setMessage("");
        }
    };

    return (
        <div className="feedback-container">
    <h1>Deixe seu Feedback</h1>
    <form onSubmit={handleSubmit}>
        <div>
            <label htmlFor="feedback">Feedback:</label>
            <textarea
                id="feedback"
                value={feedbackText}
                onChange={(e) => setFeedbackText(e.target.value)}
                required
            />
        </div>
        <button type="submit">Enviar Feedback</button>
    </form>

    {message && <p style={{ color: "green" }}>{message}</p>}
    {error && <p style={{ color: "red" }}>{error}</p>}

    <Link to="/">Ir para Home</Link>

    <h2>Feedbacks anteriores</h2>
    <div className="feedback-list">
        {feedbacks.map((fb) => (
            <div key={fb.id} className="feedback-card">
                <p className="feedback-date"><strong>Data:</strong> {fb.createdAt}</p>
                <p className="feedback-text">{fb.feedbackText}</p>
            </div>
        ))}
    </div>
</div>

    );
};

export default Feedback;
